INSERT INTO public.message (id, date_week, text, approved, username, ip, create_time, update_time, like_num, mood, weather) VALUES (398, '2020年4月27日 星期一', '你三天没回我的消息 在我孜孜不倦地骚扰下你终于舍得回我了 你说“nmsl” 我想 这一定是有什么含义吧 噢！我恍然大悟 原来是尼美舒利颗粒 她知道我关节炎 让我吃尼美舒利颗粒 你还是关心我的但是又不想显现的那么热情的 天啊 你好高冷 我好像更喜欢你了', 1, '', '', '2025-01-19 13:13:52.103', '2025-02-11 16:57:26.858', 0, 'warm', 'sunset');
