INSERT INTO public.message (id, date_week, text, approved, username, ip, create_time, update_time, like_num, mood, weather) VALUES (413, '2022年7月31日 星期天', '今天我观战了你一天带别人打游戏，你们玩得很开心，我给你发了两百多条信息，你说你没流量就不回了。晚上你发了条说说，抱怨没有人爱你。我连滚带爬评论了句有我在，你把我拉黑了。我夜里给你打电话无人接听。对不起，我不该打扰到你。我求你再给我一次当你好友的机会吧！心碎 我委屈，我想你，我会坚持我对你的爱的，你放心好啦，么么哒～我很喜欢你！', 1, '', '', '2025-01-19 13:13:59.537', '2025-02-11 16:57:28.625', 0, 'anxious', 'sunset');
