INSERT INTO public.message (id, date_week, text, approved, username, ip, create_time, update_time, like_num, mood, weather) VALUES (558, '2021年1月12日 星期二', '我暗恋的人说眼睛疼 所以我买了瓶眼药水寄过去，但她却告诉我她有喜欢的人了 让我别再打扰，距离遥远顺丰都要三天才能到，可她为什么只用了一秒就把眼药水滴进了我眼睛里', 1, '', '', '2025-01-19 13:15:18.578', '2025-02-11 17:00:00.994', 0, 'calm', 'thunderstorm');
