INSERT INTO public.message (id, date_week, text, approved, username, ip, create_time, update_time, like_num, mood, weather) VALUES (920, '2021年8月31日 星期二', '昨晚本王和朋友打了一晚上游戏，我破天荒的的给17号舔狗看了战绩，虽然他看不懂但是他相信本王一定是最厉害的，最棒的！他给本王发了好多消息，告诉我有多崇拜我，我回了他一句“啥b”。并不是因为什么，只是刚刚男神给我打电话，叫我去宾馆。哎一晚上整的我死去活来，第二天就给他打电话叫他来接我，才发现他竟然改了ID，“我是你的啥b啊”', 1, '', '', '2025-01-19 13:18:27.304', '2025-02-11 17:00:48.188', 0, 'confused', 'rainbow');
